import { useState ,useEffect } from "react";

const Input = ({setValue , value }) => {
    const [input , setInput ] = useState('')


  useEffect(() => {
    setInput(value);
  }, [value]);

  const handleChange = (e) => {
    let inputValue = e.target.value;
    // Remove non-numeric characters
    inputValue = inputValue.replace(/[^0-9]/g,'');

    // Ensure the value is within the range of 1 to 20
    if (inputValue !== '' && parseInt(inputValue) >= 1 && parseInt(inputValue) <= 20) {
      setInput(inputValue);
    }
//     else {
//     //   // Clear the input if the value is not within the range
//     //   setValue('');
//     // }

 };


  function handleclick(){
    let inputValue = input; 
    setValue(inputValue);
}

  return (
    <div>
      <label htmlFor="numberInput">Enter a number (1-20): </label>
      <input
        type="text"
        id="numberInput"
        value={value}
        onChange={handleChange}
        placeholder="1-20"
      />
      <button onClick={handleclick}></button>
    </div>
  );
};


export default Input;
