import React, { useState, useEffect } from 'react';

const WebSocketComponent = () => {
    const [id, setId] = useState('');
    const [randomNumbers, setRandomNumbers] = useState('');
    const [firstResponseReceived , setFirstResponseReceived] = useState(true)
  useEffect(() => {

    const socket = new WebSocket('ws://localhost:3000');

    // Event listener for when the connection is established
    socket.addEventListener('open', (event) => {
      console.log('WebSocket connection opened:', event);

    });

    // Event listener for incoming messages
    socket.addEventListener('message', (event) => {
      const data = JSON.parse(event.data);
        console.log(data)
        // Perform your action when the first response is received
        if (firstResponseReceived) {
            console.log('First response received taking in the id ');
            setId(data.ID);
            setFirstResponseReceived(false);
          }

        setRandomNumbers(data.value)

    });

    // Event listener for when the connection is closed
    socket.addEventListener('close', (event) => {
      console.log('WebSocket connection closed:', event);
    });

    // Cleanup function to close the socket when the component is unmounted
    return () => {
      socket.close();
    };
  }, []); // Empty dependency array ensures this effect runs only once on mount

  return (
    <div>
        <h5>Company : {`${id}`}</h5>
        <h2>Price : {`${randomNumbers}`}</h2>
    </div>
  );
};

export default WebSocketComponent;
